result:
{
  "project": "千与千寻",
  "episodes": 40,
  "overallVisualStyle": "吉卜力手绘质感与AI增强结合。核心是保留手绘的温度与笔触，利用AI增强光影的层次与色彩的微妙过渡。视觉结构上，遵循Bruce Block的视觉叙事理论，通过空间、线条、对比度的变化来外化千寻的成长弧线。从压抑的平空间构图（隧道入口、油屋初期）逐渐转向充满深度与希望的深空间构图（海上铁路、钱婆婆小屋）。",
  "tenCinematicLightingTypes": [
    {
      "type": "Mystery Lighting (神秘光)",
      "application": "隧道入口、油屋初次登场。采用John Alton的'少即是多'原则，单束强侧光或顶光，仅照亮关键路径或建筑局部，大部分细节沉入阴影，营造未知与不安。",
      "exampleScene": "隧道入口"
    },
    {
      "type": "Mood Lighting - Oppression (情绪光-压迫)",
      "application": "汤婆婆办公室、初入油屋走廊。低角度光投射巨大、变形的阴影（参考Gordon Willis的《教父》），高对比度，角色被阴影吞噬，外化千寻的渺小与恐惧。",
      "exampleScene": "汤婆婆办公室"
    },
    {
      "type": "Follow Focus Light (聚焦光)",
      "application": "锅炉房、大浴场。在复杂场景中，光如画笔引导视线。锅炉爷爷众多手臂中，只照亮正在工作的那几双；大浴场中，光束聚焦于千寻或某位客人，其余区域沉浸于琥珀色暖雾中。",
      "exampleScene": "锅炉房"
    },
    {
      "type": "Atmospheric Haze (氛围光雾)",
      "application": "油屋外观夜景、大浴场。利用AI增强吉卜力特色的光雾质感。深夜靛蓝背景中，油屋灯火透过蒸汽形成体积光，色彩遵循Patti Bellantoni的色彩心理学，暖光代表诱惑与危险并存。",
      "exampleScene": "油屋外观"
    },
    {
      "type": "Practical Source Lighting (实用光源照明)",
      "application": "锅炉房的炉火、钱婆婆小屋的台灯。光源即故事。炉火的跳动红光（危险、活力）与台灯稳定的暖黄光（智慧、温暖）形成叙事对比。",
      "exampleScene": "钱婆婆小屋"
    },
    {
      "type": "Silhouette & Backlight (剪影与逆光)",
      "application": "海上铁路、隧道出口。新海诚风格的强烈逆光，勾勒角色轮廓，与记忆金色结合，营造神圣、回忆与希望的巅峰时刻。",
      "exampleScene": "海上铁路"
    },
    {
      "type": "Mood Lighting - Duality (情绪光-双重性)",
      "application": "角色内心冲突时刻（如白龙回忆）。面部采用经典的“双面人”布光，一半在暖光（人性/记忆），一半在冷光（诅咒/遗忘），参考Roger Deakins的细腻层次。",
      "exampleScene": "角色对话关键帧"
    },
    {
      "type": "Top Light / God Light (顶光/神圣光)",
      "application": "钱婆婆纺织、结局。柔和顶光洒下，如启示或祝福，降低对比度，营造安全、宁静与治愈的氛围。",
      "exampleScene": "钱婆婆小屋内部"
    },
    {
      "type": "Chiaroscuro (明暗对比法)",
      "application": "油屋的深夜走廊、隐秘角落。极致的黑白对比，深黑色阴影中仅有狭窄的高光带，强化神秘世界的诡异与规则森严。",
      "exampleScene": "油屋夜间走廊"
    },
    {
      "type": "Memory Glow (记忆辉光)",
      "application": "千寻回忆现实、白龙想起名字。整体画面泛着柔和的“记忆金色”，降低饱和度，提高光晕，模仿旧照片或褪色记忆的质感，与主故事线的浓郁色彩形成视觉结构上的对比。",
      "exampleScene": "回忆片段"
    }
  ],
  "lightingEmotionChart": [
    {
      "emotion": "恐惧/未知",
      "lightingType": "Mystery Lighting, Chiaroscuro",
      "colorTemperature": "冷色偏青 (Cyan) / 低色温琥珀 (阴影部分)",
      "contrast": "极高",
      "example": "隧道入口，阴影浓重，仅一束冷光"
    },
    {
      "emotion": "压迫/规则",
      "lightingType": "Mood Lighting - Oppression",
      "colorTemperature": "暖琥珀色 (但沉重)",
      "contrast": "高",
      "example": "汤婆婆办公室，低角度光，巨大阴影"
    },
    {
      "emotion": "忙碌/诱惑",
      "lightingType": "Atmospheric Haze, Practical Source",
      "colorTemperature": "暖琥珀色",
      "contrast": "中低，雾化柔化",
      "example": "大浴场全景，蒸汽弥漫，灯火通明"
    },
    {
      "emotion": "温暖/治愈",
      "lightingType": "Practical Source, Top Light",
      "colorTemperature": "暖黄色/记忆金色",
      "contrast": "低至中",
      "example": "钱婆婆小屋，台灯光晕，均匀柔和"
    },
    {
      "emotion": "希望/升华",
      "lightingType": "Silhouette & Backlight, Memory Glow",
      "colorTemperature": "金色/玫瑰金色",
      "contrast": "中高 (强调轮廓)",
      "example": "海上铁路，逆光中的千寻与无脸男"
    },
    {
      "emotion": "孤独/思索",
      "lightingType": "Follow Focus Light",
      "colorTemperature": "冷暖混合 (角色暖，环境冷)",
      "contrast": "中",
      "example": "千寻夜晚独坐窗边，一束光打亮她"
    }
  ],
  "masterStyleReferences": {
    "RogerDeakins": "应用于复杂室内场景的光影层次与质感。如锅炉房的机械光泽、蒸汽的细微折射，追求真实光源下的极致细腻，避免CG感。",
    "GordonWillis": "应用于权力与压抑场景。汤婆婆办公室的‘黑暗王子’风格，让阴影占据主导，仅用少量硬光揭示权力象征（如印章、珠宝），营造深不可测的威严。",
    "MakotoShinkai": "应用于情感高潮与风景场景。海上铁路的水面反射、天空的渐变云霞，使用高饱和度、高透明度的强烈逆光，创造‘世界系’的浪漫与宏大感。"
  },
  "colorSystem": {
    "corePalette": ["琥珀色温泉暖光", "深夜靛蓝", "记忆金色"],
    "colorEmotionMapping": [
      {
        "color": "琥珀色温泉暖光",
        "emotions": ["诱惑", "忙碌", "虚假的温暖", "欲望", "行业规则"],
        "psychology": "根据Bellantoni，暖色吸引人但也暗示危险。油屋的暖光是吸引神灵的陷阱，也是千寻必须工作的环境色。",
        "application": "大浴场主色调、油屋内景。与冷色阴影形成冷暖对比，突出空间的纵深与复杂。"
      },
      {
        "color": "深夜靛蓝",
        "emotions": ["神秘", "孤独", "恐惧", "超自然", "静谧"],
        "psychology": "蓝色关联冷静与忧郁，深靛蓝则代表未知的魔法世界与夜晚的规则。",
        "application": "油屋外观夜景、神秘世界天空、水域。作为暖光的互补色，奠定整体基调的冷峻一面。"
      },
      {
        "color": "记忆金色",
        "emotions": ["希望", "纯真", "回忆", "友谊", "真实姓名"],
        "psychology": "黄色小剂量带来温暖与希望。褪色的‘记忆金色’象征即将消失但珍贵的真实世界与情感。",
        "application": "关键情感时刻（白龙忆名）、现实世界闪回、钱婆婆的线。在冷峻基调中作为视觉高潮的点睛色。"
      }
    ],
    "supportingColors": [
      {
        "color": "锅炉房赤红",
        "role": "危险与活力。炉火、煤炭的红光，象征锅炉爷爷的力量与工作的危险。",
        "emotion": "能量、危险、温暖（双重性）"
      },
      {
        "color": "腐败神淤泥黑绿",
        "role": "疾病与净化。运用病态黄绿（Bellantoni的疾病色）与黑色的混合，视觉上令人不适，净化后转为清澈。",
        "emotion": "腐烂、污染、而后解脱"
      },
      {
        "color": "无脸男吞噬暗紫",
        "role": "空虚与膨胀的欲望。当他膨胀时，环境色加入不稳定的暗紫色，暗示内心的混乱与空洞。",
        "emotion": "贪婪、迷失、孤独"
      }
    ],
    "冷暖对比Design": "全片以‘深夜靛蓝’为冷基底，‘琥珀暖光’为热区，形成基础张力。千寻的成长线通过色彩参与：初期她淹没在冷暖对比强烈的环境中（被支配）-> 中期她开始携带‘记忆金色’（希望萌芽）-> 后期她能影响环境色（如为河神净化带来清澈），最终在海上铁路，她与‘记忆金色’的逆光融为一体（成为主导）。"
  },
  "keyScenesDesignGuide": {
    "油屋外观": "视觉结构：深空间，突出建筑巨大压迫感。光影：夜间采用Atmospheric Haze，靛蓝夜空下，无数暖黄窗口透出光雾，形成强烈的冷暖与明暗对比。色彩：琥珀与靛蓝的经典对决。",
    "锅炉房": "视觉结构：拥挤的平空间，压缩感。光影：Follow Focus Light + Practical Source。主光源是炉火，红光跳动，只照亮工作区域，其余是充满器械剪影的阴影。色彩：赤红主导，阴影为暖黑。",
    "大浴场": "视觉结构：开场为深空间展现场面，千寻工作时转为中近景平空间（强调忙碌与重复）。光影：弥漫的Atmospheric Haze，光束穿过蒸汽。色彩：统一的琥珀暖光，通过客人（如河神）引入对比色。",
    "汤婆婆办公室": "视觉结构：极致的平空间与对称构图，制造囚禁感。光影：Mood Lighting - Oppression，参考Gordon Willis。大办公桌后是阴影，仅台灯照亮文件与珠宝。色彩：奢华但沉重的暖色调，点缀金色。",
    "海上铁路": "视觉结构：开阔的深空间，水平线构图突显宁静。光影：Silhouette & Backlight的巅峰应用，新海诚风格。水面反射天空的玫瑰金与蓝色。色彩：记忆金色与日落粉紫的渐变，纯净清澈。",
    "钱婆婆小屋": "视觉结构：温馨的、有纵深的深空间（与汤婆婆办公室对比）。光影：Practical Source + Top Light，均匀柔和。色彩：记忆金色与木质暖褐为主，点缀纺织品的柔和色彩。",
    "隧道入口": "视觉结构：从现实世界的平空间（汽车）转向神秘世界的深空间（隧道），完成视觉转换。光影：Mystery Lighting，洞口如画框，内部一片漆黑，仅有入口处的侧光勾勒轮廓。色彩：现实世界灰绿色调，隧道口是冷暖交界处。"
  }
}
